changeSet.createProcedure:
CREATE OR REPLACE FUNCTION ${database.defaultSchemaName}.trigger_exists(name text)
  RETURNS text AS $$
DECLARE
  rowCount int;
BEGIN
  SELECT COUNT(*) INTO rowCount
    FROM pg_trigger
    WHERE tgname = name;

  IF rowCount = 1 THEN
    RETURN 'true';
  ELSE
    RETURN 'false';
  END IF;
END;
$$ LANGUAGE 'plpgsql';
