changeSet.sql:
INSERT INTO kb_users (id, user_name, first_name, middle_name, last_name, patron_group)
SELECT id, user_name, first_name, middle_name, last_name, patron_group
FROM assigned_users;

INSERT INTO kb_users (id, user_name, first_name, middle_name, last_name)
SELECT created_by_user_id, created_by_username, created_by_first_name, created_by_middle_name, created_by_last_name
FROM access_types ON CONFLICT(id) DO NOTHING;

INSERT INTO kb_users (id, user_name, first_name, middle_name, last_name)
SELECT updated_by_user_id, updated_by_username, updated_by_first_name, updated_by_middle_name, updated_by_last_name
FROM access_types ON CONFLICT(id) DO NOTHING;
